package com.healthfirst.classesservice.events;import com.healthfirst.classesservice.models.ClassInfo;import com.healthfirst.classesservice.services.SequenceGeneratorService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.mongodb.core.mapping.event.AbstractMongoEventListener;import org.springframework.data.mongodb.core.mapping.event.BeforeConvertEvent;import org.springframework.stereotype.Component;@Componentpublic class ClassInfoModelListener extends AbstractMongoEventListener<ClassInfo> {  private SequenceGeneratorService sequenceGenerator;  @Autowired  public ClassInfoModelListener(SequenceGeneratorService sequenceGenerator) {    this.sequenceGenerator = sequenceGenerator;  }  @Override  public void onBeforeConvert(BeforeConvertEvent<ClassInfo> event) {    if (event.getSource().getId() == null) {      event.getSource().setId(sequenceGenerator.generateSequence(ClassInfo.SEQUENCE_NAME));    }  }}