package com.healthfirst.emailservice.validation;import com.healthfirst.emailservice.enums.ClassType;import java.util.Arrays;import javax.validation.ConstraintValidator;import javax.validation.ConstraintValidatorContext;public class ClassTypeSubSetValidator implements ConstraintValidator<ClassTypeSubset, ClassType> {  private ClassType[] subset;  @Override  public void initialize(ClassTypeSubset constraint) {    this.subset = constraint.anyOf();  }  @Override  public boolean isValid(ClassType value, ConstraintValidatorContext context) {    return value == null || Arrays.asList(subset).contains(value);  }}