package com.healthfirst.welcomeservice.validation;import com.healthfirst.welcomeservice.enums.Interest;import java.util.Arrays;import javax.validation.ConstraintValidator;import javax.validation.ConstraintValidatorContext;public class InterestTypeSubSetValidator implements ConstraintValidator<InterestTypeSubset, Interest> {  private Interest[] subset;  @Override  public void initialize(InterestTypeSubset constraint) {    this.subset = constraint.anyOf();  }  @Override  public boolean isValid(Interest value, ConstraintValidatorContext context) {    return value == null || Arrays.asList(subset).contains(value);  }}